commits.content:
*{
      margin: 0px;
      padding: 0;
      box-sizing: border-box;
      font-family: 'Open Sans', sans-serif;
}

body{
      display:flex;
      justify-content: center;
      align-items: center;
      height: 100vh;
      width: 100vw;
      background: url("https://source.unsplash.com/1600x900/?landscape")
}
.caixa-maior{

      background-color: #000000;
      width: 95%;
      max-width: 420px;
      opacity: 0.8;
      padding: 20px;
      border-radius: 25px;
}

h2{
      color: #ffffff;
      font-size: 28px;
      margin-top: 40px;
      margin-left: 0px;
      margin-right: 0px;
      margin-bottom: 40px;
}

.temp {
     color: #ffffff;
     margin-top: 40px;
     margin-left: 0px;
     margin-right: 0px;
     margin-bottom: 40px;
}
.descricao {

     color: #ffffff;
     margin-top: 40px;
     margin-left: 0px;
     margin-right: 0px;
     margin-bottom: 40px;
}
.umidade {

     color: #ffffff;
     margin-top: 40px;
     margin-left: 0px;
     margin-right: 0px;
     margin-bottom: 40px;
}

input{
     border: none;
     outline: none;
     padding: 10px;
     border-radius: 24px;
     font-size: 20px;
     background-color: #7c7c7c2b;
     color: white;
     width: calc(100% - 100px);
}

.lupa {
     height: 25px;
     /*background-color: #7c7c7c2b;*/
}

button{
     border: none;
     outline: none;
     padding: 10px;
     border-radius: 50px;
     background-color:#7c7c7c2b ;
     float: right;
     cursor: pointer;
}

button:hover {
     background-color: #7c7c7c6b;
}

.caixa-media {
     margin-top: 20px;
}
